# VPS Deployment Verification Script# This script checks if all website files are present on the VPSWrite-Host "=== Last Drop VPS Deployment Check ===" -ForegroundColor CyanWrite-Host ""# Read SSH config to find the host$sshConfigPath = "$env:USERPROFILE\.ssh\config"Write-Host "Checking SSH config at: $sshConfigPath" -ForegroundColor Yellowif (Test-Path $sshConfigPath) {    Write-Host "SSH config found!" -ForegroundColor Green    Write-Host ""        # Display available hosts    Write-Host "Available SSH hosts:" -ForegroundColor Cyan    Get-Content $sshConfigPath | Select-String "^Host " | ForEach-Object { Write-Host "  - $($_.Line)" }    Write-Host ""}# Prompt for SSH host$sshHost = Read-Host "Enter SSH host name from config (e.g., 'lastdrop' or 'vps')"Write-Host ""Write-Host "=== Checking VPS Connection ===" -ForegroundColor Cyan# Test connectionWrite-Host "Testing SSH connection to $sshHost..." -ForegroundColor Yellowssh $sshHost "echo 'Connection successful!'"if ($LASTEXITCODE -eq 0) {    Write-Host "Connection working!" -ForegroundColor Green    Write-Host ""        # Check web root    Write-Host "=== Checking Web Directory ===" -ForegroundColor Cyan    $webRoot = Read-Host "Enter web root path (default: /var/www/html)"    if ([string]::IsNullOrWhiteSpace($webRoot)) {        $webRoot = "/var/www/html"    }        Write-Host ""    Write-Host "Checking directory: $webRoot" -ForegroundColor Yellow    ssh $sshHost "ls -la $webRoot"        Write-Host ""    Write-Host "=== Checking PHP Files ===" -ForegroundColor Cyan    ssh $sshHost "find $webRoot -name '*.php' -type f | head -20"        Write-Host ""    Write-Host "=== Checking HTML Files ===" -ForegroundColor Cyan    ssh $sshHost "find $webRoot -name '*.html' -type f | head -20"        Write-Host ""    Write-Host "=== Checking API Directory ===" -ForegroundColor Cyan    ssh $sshHost "ls -la $webRoot/api/"        Write-Host ""    Write-Host "=== Checking Database Schema ===" -ForegroundColor Cyan    ssh $sshHost "ls -la $webRoot/api/database_schema.sql"        Write-Host ""    Write-Host "=== Checking Permissions ===" -ForegroundColor Cyan    ssh $sshHost "ls -ld $webRoot"    ssh $sshHost "ls -ld $webRoot/api"        Write-Host ""    Write-Host "=== Testing PHP ===" -ForegroundColor Cyan    ssh $sshHost "php -v"        Write-Host ""    Write-Host "=== Checking Recent Files ===" -ForegroundColor Cyan    Write-Host "Most recently modified files:" -ForegroundColor Yellow    ssh $sshHost "find $webRoot -type f -mtime -7 -ls | tail -10"    } else {    Write-Host "SSH connection failed!" -ForegroundColor Red    Write-Host "Please check:" -ForegroundColor Yellow    Write-Host "  1. SSH host name is correct" -ForegroundColor White    Write-Host "  2. SSH key is loaded (ssh-add -l)" -ForegroundColor White    Write-Host "  3. VPS is accessible" -ForegroundColor White}Write-Host ""Write-Host "=== Check Complete ===" -ForegroundColor Cyan